SET exp = #{exp} + 100
WHERE userKey = #{userKey}

UPDATE review
SET viewCnt = #{viewCnt} + 1
WHERE reviewKey = #{reviewKey}

mapper.select:
SELECT userKey
FROM study_user_review
WHERE reviewKey = #{reviewKey}

