mapper.select:
SELECT todoKey
FROM study_todo
WHERE studyKey = #{studyKey}

SELECT scheduleKey
FROM study_schedule
WHERE studyKey = #{studyKey}

SELECT todoKey
FROM study_todo
WHERE studyKey = #{studyKey}

mapper.update:
UPDATE study 
SET studyName = #{studyName}, category = #{category}, studyEnd = #{studyEnd}, alarm = #{alarm}
WHERE studyKey = #{studyKey}
